package com.lec.ex;

public class VarEx01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10;
		byte j = 10 ;
		double h = 10.15;
		i = 5;
		System.out.println("i=" +i + "\t j ="+j +"\t h ="+ h);
		char c1 = 'A';
		char c2 = 'B';
		char c3 = 'C';
		System.out.print("c1="+ c1 + "\t c2="+ c2 + "\t c3=" + c3);
		System.out.printf("c1=%c \t c2= %c\t c3= %c\n", c1, c2, c3);
	}

}
